import React, { Component } from "react";

class Person extends Component {
  componentDidUpdate(prevProps) {
    if (prevProps.person.show !== this.props.person.show && !this.props.person.show) {
      alert(`A person with ${this.props.person.email} was removed from the network`);
    }
  }

  render() {
    const { img, email, show } = this.props.person;
    if (!show) {
      return null; // Do not render if show is false
    }

    return (
      <div className="person">
        <b onClick={() => this.props.handleRemove(this.props.index)}>X</b>
        <img alt={email} src={img} />
        <p>{email}</p>
      </div>
    );
  }
}

export default Person;
